import React, { useState } from 'react';
import { AppBar, Toolbar, Button, IconButton, Drawer, List, ListItem, ListItemText, useMediaQuery, useTheme } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import { Link } from 'react-scroll';

const navItems = [
  { name: 'Experience', to: 'experience' },
  { name: 'Skills', to: 'skills' },
  { name: 'Education', to: 'education' },
  { name: 'Projects', to: 'projects' },
  { name: 'Contact', to: 'contact' },
];

const Header = () => {
  const [mobileOpen, setMobileOpen] = useState(false);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    <List>
      {navItems.map((item) => (
        <ListItem key={item.name} onClick={handleDrawerToggle}>
          <Link
            to={item.to}
            smooth={true}
            duration={500}
            style={{ textDecoration: 'none', width: '100%' }}
          >
            <ListItemText primary={item.name} />
          </Link>
        </ListItem>
      ))}
    </List>
  );

  return (
    <AppBar position="fixed" color="transparent" elevation={0} sx={{ backdropFilter: 'blur(10px)' }}>
      <Toolbar>
        <div style={{ flexGrow: 1 }}>
          <Link to="hero" smooth={true} duration={500} style={{ textDecoration: 'none' }}>
            <Button color="primary">Your Name</Button>
          </Link>
        </div>

        {isMobile ? (
          <>
            <IconButton
              color="inherit"
              aria-label="open drawer"
              edge="end"
              onClick={handleDrawerToggle}
            >
              <MenuIcon />
            </IconButton>
            <Drawer
              anchor="right"
              open={mobileOpen}
              onClose={handleDrawerToggle}
            >
              {drawer}
            </Drawer>
          </>
        ) : (
          <div>
            {navItems.map((item) => (
              <Link
                key={item.name}
                to={item.to}
                smooth={true}
                duration={500}
                style={{ textDecoration: 'none' }}
              >
                <Button color="inherit" sx={{ mx: 1 }}>
                  {item.name}
                </Button>
              </Link>
            ))}
          </div>
        )}
      </Toolbar>
    </AppBar>
  );
};

export default Header;
